// pages/Support.js
import React from 'react';
import { Box, Heading, Text, Button, Input, FormControl, FormLabel, FormHelperText } from '@chakra-ui/react';
import Select, { StylesConfig } from 'react-select';
import ArrowLeftIcon from '../public/arrow-left.png'
function Support() {
  const countryOptions = [
    { value: 'usa', label: 'United States' },
    { value: 'canada', label: 'Canada' },
    // Add more country options
  ];
  const customStyles: StylesConfig = {
    control: (provided) => ({
      ...provided,
      textAlign: 'left', // Align the text to the left
      borderColor: "#CCCCCC",
      py:"14",
      _hover: { borderColor: 'gray.600' },
    }),
    placeholder: (provided) => ({
      ...provided,
      paddingTop:"14px",
      paddingBottom:"14px",
      color:"#545454",
      paddingLeft: '8px', // Add left padding to the placeholder
    }),
  };
  return (
    <Box bgColor="white">
       <Link to="/">
        <img
          src={backArrowIcon}
          alt="Back"
          style={{ position: 'absolute', top: '20px', left: '20px', width: '24px', cursor: 'pointer' }}
        />
      </Link>
    <Box textAlign="center" maxWidth="700px" m="auto" bgColor="white" p={10} borderColor='gray.600'>
      <Heading color="black" mb="4" >Support Quaapay</Heading>
      <Text mt={3} color="black" maxWidth="480px" m="auto">Support quaapay company to create more massive features you will enjoy later on.</Text>


<Box mt="8">
      <Button variant="outline" borderColor=" gray.600"  color="black" w='49.3%'  mt={5} mr={2} py={"26px"}>
        Give Once
      </Button> 
      <Button variant="outline" borderColor=" gray.600"  color="black" w="49.3%" mt={5} ml={0} py={"26px"}>
       Give Monthly
      </Button>
      </Box>

      <form >
        <FormControl mt={12} color="black">
          <Input type="email"  borderColor="#CCCCCC"
          placeholder="Name"
          py="28px"
          _placeholder={{color:'#545454'}}
          _hover={{borderColor:"gray.600"}}
          />
          
        </FormControl>

        <FormControl mt={8} color="black">
          <Input type="email"  borderColor="#CCCCCC"
          placeholder="Email address"
          py="28px"
          _placeholder={{color:'#545454'}}
          _hover={{borderColor:"gray.600"}}
          />
        </FormControl>

        <FormControl mt={8} color="black">
            {/* Use the Select component for the dropdown */}
            <Select options={countryOptions} styles={customStyles} placeholder="Select a country"
            // placeholder={{textAlign:'right'}}
            />
          </FormControl>



        <Button type="submit" w="100%" bgColor="#4335E2" _hover={{bgColor:"#4335E2"}} py='6' mt={6}>
          Continue
        </Button>
      </form>
    </Box>
    </Box>
  );
}

export default Support;
