// pages/Support.js
import React from 'react';
import {Link} from 'react-router-dom'
import { ChakraProvider, extendTheme, CSSReset,Box, Heading,Image, Text, Button, Input, FormControl, FormLabel, FormHelperText } from '@chakra-ui/react';
import Select, { StylesConfig } from 'react-select';
import ArrowLeftIcon from './arrow-left.png';
import Successful from './success.png'
const theme = extendTheme({
  components: {
    Input: {
      baseStyle: {
        _focus: {
          boxShadow: 'none', // Remove box shadow on focus
        },
      },
    },
  },
});
function successpay() {
  const countryOptions = [
    { value: 'usa', label: 'United States' },
    { value: 'canada', label: 'Canada' },
    // Add more country options
  ];
  const customStyles: StylesConfig = {
    control: (provided) => ({
      ...provided,
      textAlign: 'left', // Align the text to the left
      borderColor: "#CCCCCC",
      py:"14",
      _hover: { borderColor: 'gray.600' },
      boxShadow:"none" // Remove box shadow

    }),
    placeholder: (provided) => ({
      ...provided,
      paddingTop:"14px",
      paddingBottom:"14px",
      color:"#545454",
      paddingLeft: '8px', // Add left padding to the placeholder
    }),
  };
  return (
    <Box bgColor="white">
      
       <Link to="/">
        <img
          src={ArrowLeftIcon}
          alt="Back"
          style={{ position: 'absolute', top: '10px', left:'10px', margin: '0 45px 12px 12px ' , width: '44px', cursor: 'pointer' }}
        />
      </Link>

      <img src={Successful}  alt="" />
    <Box  maxWidth="700px" m="auto" bgColor="white" p={{base:'2', sm:'4', lg:'10'}} borderColor='gray.600'>
      <Text color="black"  mb="4" textAlign="center" fontSize="34px" fontWeight="900">Payment successful</Text>
      <Text mt={3}  color="black" maxWidth="480px" textAlign="center" m="auto">Thanks so much for supporting Quapaay! weâ€™ll be in touch with you soon.</Text>


    <Button  w="100%" bgColor="#4335E2" _hover={{bgColor:"#4335E2"}} py='6' mt={6}>
          Back Home
        </Button>
    </Box>
    </Box>
  );
}

export default successpay;
