// pages/Support.js
import React from 'react';
import { Box, Heading, Text, Button, Input, FormControl, FormLabel, FormHelperText } from '@chakra-ui/react';
import {Select} from 'react-select';
function Support() {
  const countryOptions = [
    { value: 'usa', label: 'United States' },
    { value: 'canada', label: 'Canada' },
    // Add more country options
  ];
  const customStyles = {
    control: (provided) => ({
      ...provided,
      textAlign: 'left', // Align the text to the left
      borderColor: 'gray.400',
      _hover: { borderColor: 'gray.600' },
    }),
    placeholder: (provided) => ({
      ...provided,
      paddingLeft: '8px', // Add left padding to the placeholder
    }),
  };
  return (
    <Box bgColor="white">
    <Box textAlign="center" maxWidth="700px" m="auto" bgColor="white" p={10} borderColor='gray.600'>
      <Heading color="black" >Support Quaapay</Heading>
      <Text mt={3} color="black">Support quaapay company to create more massive features you will enjoy later on.</Text>


<Box w="100%">
      <Button variant="outline" borderColor=" gray.600"  color="black" w='45%'  mt={5} mr={2}>
        Give Once
      </Button>
      <Button variant="outline" borderColor=" gray.600"  color="black" w="45%" mt={5} ml={2}>
       Give Monthly
      </Button>
      </Box>

      <form >
        <FormControl mt={12} color="black">
          <Input type="email"  borderColor="gray.400"
          placeholder="Name"
          py="28px"
          _placeholder={{color:'#545454'}}
          _hover={{borderColor:"gray.600"}}
          />
          
        </FormControl>

        <FormControl mt={8} color="black">
          <Input type="email"  borderColor="gray.400"
          placeholder="Email address"
          py="28px"
          _placeholder={{color:'#545454'}}
          _hover={{borderColor:"gray.600"}}
          />
        </FormControl>

        <FormControl mt={12} color="black">
            {/* Use the Select component for the dropdown */}
            <Select options={countryOptions} styles={customStyles} placeholder="Select a country"
            // placeholder={{textAlign:'right'}}
            />
          </FormControl>



        <Button type="submit" w="100%" bgColor="#4335E2" py='6' mt={6}>
          Continue
        </Button>
      </form>
    </Box>
    </Box>
  );
}

export default Support;
